<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>TZ</title>
    <style>
    /* modificator */
    .hidden {
        display: none;
    }

    /* modificator end */

    .swiper-slide {
        background-attachment: fixed;
        background-image: url("{{ asset('/images/Framebunner.jpg') }}");
    }

    .bodyOverlay {
        background-color: rgba(0, 0, 0, 0.5);
    }

    .header-menu {
        display: block;
    }

    .header-menu-mobile {
        display: none;
    }

    .header-phone-content {
        display: flex;
        align-items: center;
    }

    .header-contacts-content {
        display: flex;
        align-items: center;
    }

    .header-time-content {
        display: flex;
        align-items: center;
    }

    /* FOOTER CONTENT */
    .footer-block-price-offer {
        display: grid;
    }

    .footer-block-price-offer-mobile {
        display: none;
    }

    /* FOOTER CONTENT END */

    @media (max-width: 768px) {

        /* HEADER MENU */
        .header-menu {
            display: none;
        }

        .header-menu-mobile {
            display: block;
        }

        /* HEADER MENU END*/
        /* MAIN CONTENT */
        .block-diet--title {
            padding-left: 20px;
        }

        /* MAIN CONTENT END*/

        /* FOOTER CONTENT */
        .footer-block-contacts {
            display: none;
        }

        .footer-block-price-offer {
            display: grid;
        }

        .footer-block-price-offer-mobile {
            display: none;
        }


        /* FOOTER CONTENT END*/
    }

    @media (max-width: 560px) {

        /* HEADER MENU */
        .header-phone-content {
            margin-right: 42px;
        }

        .header-contacts-content {
            display: none;
        }

        .header-time-content {
            display: none;
        }

        /* HEADER MENU END*/

        .contact_card {
            width: max-content;
        }

        .contact_card_frame {
            width: auto;
        }
    }

    @media (max-width:468px) {

        /* HEADER CONTENT MOBILE */
        .header_top {
            gap: 12px;
        }

        .header-top--logo {
            margin-left: 8px;
        }

        .header-phone-content {
            margin-right: 0;
            margin-left: 8px;
        }

        .menu-mobile-content {
            margin-left: 8px;
        }

        /* HEADER CONTENT MOBILE END*/

        /* FOOTER CONTENT */
        .footer-block-price-offer {
            display: none;
        }

        .footer-block-price-offer-mobile {
            display: flex;
            align-items: center;
        }

        /* FOOTER CONTENT END*/
    }
    </style>
</head>

